(2, 1, NULL, '2013-01-21', 'Filtered Recipe Results', 'tagged', 99, 1, 1, NULL, NULL, NULL, NULL, 'original', NULL, 'recipe_filtered_list', NULL, NULL, NULL, NULL, NULL, 41, NULL),
(5, 1, NULL, '2013-01-29', 'Our Recipes', 'list', 99, 1, 1, NULL, NULL, NULL, NULL, 'original', NULL, 'recipe_home', NULL, 'blonde-fudge', 'Mmm...<br /> Blonde Fudge', NULL, '<p>Enjoy searching through our mouthwatering selection of recipes, using our categories to help you hone &nbsp;in on a particular dish. From marvellous mains and dazzling desserts to sumptuous sweet treats, you''re sure to find the perfect recipe!</p>', 40, NULL),
(11, 1, 36, '2013-02-25', 'Afternoon Tea', 'afternoon-tea', 7, 1, 1, NULL, NULL, NULL, '<p class="largeBlue">Treats for Afternoon tea</p>\r\n<p>Fancy a little nibble of something sweet with your cuppa?</p>\r\n<p>We&rsquo;ve got a tantilising collection of bite size treats to go hand in hand with afternoon tea time.</p>', NULL, 'teapotItem', 'recipe_cat_page', NULL, 'afternoon-tea', 'Tea <br> & scones', NULL, '<p><!--[if gte mso 9]><xml>\r\n <o:DocumentProperties>\r\n  <o:Template>Normal.dotm</o:Template>\r\n  <o:Revision>0</o:Revision>\r\n  <o:TotalTime>0</o:TotalTime>\r\n  <o:Pages>1</o:Pages>\r\n  <o:Words>15</o:Words>\r\n  <o:Characters>91</o:Characters>\r\n  <o:Company>mm</o:Company>\r\n  <o:Lines>1</o:Lines>\r\n  <o:Paragraphs>1</o:Paragraphs>\r\n  <o:CharactersWithSpaces>111</o:CharactersWithSpaces>\r\n  <o:Version>12.0</o:Version>\r\n </o:DocumentProperties>\r\n <o:OfficeDocumentSettings>\r\n  <o:AllowPNG/>\r\n </o:OfficeDocumentSettings>\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n <w:WordDocument>\r\n  <w:Zoom>0</w:Zoom>\r\n  <w:TrackMoves>false</w:TrackMoves>\r\n  <w:TrackFormatting/>\r\n  <w:PunctuationKerning/>\r\n  <w:DrawingGridHorizontalSpacing>18 pt</w:DrawingGridHorizontalSpacing>\r\n  <w:DrawingGridVerticalSpacing>18 pt</w:DrawingGridVerticalSpacing>\r\n  <w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery>\r\n  <w:DisplayVerticalDrawingGridEvery>0</w:DisplayVerticalDrawingGridEvery>\r\n  <w:ValidateAgainstSchemas/>\r\n  <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid>\r\n  <w:IgnoreMixedContent>false</w:IgnoreMixedContent>\r\n  <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText>\r\n  <w:Compatibility>\r\n   <w:BreakWrappedTables/>\r\n   <w:DontGrowAutofit/>\r\n   <w:DontAutofitConstrainedTables/>\r\n   <w:DontVertAlignInTxbx/>\r\n  </w:Compatibility>\r\n </w:WordDocument>\r\n</xml><![endif]--><!--[if gte mso 9]><xml>\r\n <w:LatentStyles DefLockedState="false" LatentStyleCount="276">\r\n </w:LatentStyles>\r\n